const s10chLocalStorageKey = "s10ch";

const baslangicDegerleri = {
  notlar: [
    {
      id: "75g1IyB8JLehAr0Lr5v3p",
      date: "Fri Feb 03 2023 09:40:27 GMT+0300 (GMT+03:00)",
      body: "Bugün hava çok güzel!|En iyi arkadaşımın en iyi arkadaşı olduğumu öğrendim :)|Kedim iyileşti!",
    },
  ],
};

function localStorageStateYaz(key, data) {
  localStorage.setItem(key, JSON.stringify(data));
}

function localStorageStateOku(key) {
  return JSON.parse(localStorage.getItem(key));
}

function baslangicNotlariniGetir(key) {
  const eskiNotlar = localStorage.getItem(key);

  if (eskiNotlar) {
    return localStorageStateOku(key);
  } else {
    return baslangicDegerleri
  }
}

export function myReducer(state = initial, action) {
  switch (action.type) {
    case FAV_ADD:
      if (!state.favs.includes(action.payload)) {
        const newFavs = [...state.favs, action.payload];
        writeFavsToLocalStorage({ ...state, favs: newFavs });
        toast.success("favorilere eklendi", {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
        return { ...state, favs: newFavs };
      } else {
        return state
      }
    case FAV_REMOVE:
      const updatedFavs = state.favs.filter((fav) => fav.key !== action.payload);
      writeFavsToLocalStorage({ ...state, favs: updatedFavs });
      toast.success("favorilerden çıkarıldı")
      return { ...state, favs: updatedFavs };

    case FETCH_SUCCESS:
      return { ...state, loading: false, current: action.payload };

    case FETCH_LOADING:
      return { ...state, loading: true, error: '' };

    case FETCH_ERROR:
      toast.error(action.payload)
      return { ...state, loading: false, error: action.payload };

    case GET_FAVS_FROM_LS:
      return { ...state, favs: readFavsFromLocalStorage() || [] };

    case RESET_LOCAL:
      writeFavsToLocalStorage({ ...state, favs: [] });
      return { ...state, favs: [] };

    default:
      return state;
  }
}
